void TIMERViewBase::flexButtonCallbackHandler(const touchgfx::AbstractButtonContainer& src)
{
    if (&src == &mainBtn)
    {
        //Interaction1
        //When mainBtn clicked change screen to MAIN
        //Go to MAIN with no screen transition
        application().gotoMAINScreenNoTransition();
    }
    if (&src == &hPlusBtn)
    {
        //Interaction2
        //When hPlusBtn clicked call virtual function
        //Call plusHour
        plusHour();
    }
    if (&src == &hMinusBtn)
    {
        //Interaction3
        //When hMinusBtn clicked call virtual function
        //Call minusHour
        minusHour();
    }
    if (&src == &mPlusBtn)
    {
        //Interaction4
        //When mPlusBtn clicked call virtual function
        //Call plusMinute
        plusMinute();
    }
    if (&src == &mMinusBtn)
    {
        //Interaction5
        //When mMinusBtn clicked call virtual function
        //Call minusMinute
        minusMinute();
    }
    if (&src == &setBtn)
    {
        //Interaction6
        //When setBtn clicked call virtual function
        //Call setTimer
        setTimer();
    }
    if (&src == &onSetBtn_OFF)
    {
        //Interaction7
        //When onSetBtn_OFF clicked call virtual function
        //Call onSet
        onSet();
    }
    if (&src == &offSetBtn_ON)
    {
        //Interaction8
        //When offSetBtn_ON clicked call virtual function
        //Call offSet
        offSet();
    }
}
